mapper.insert:
insert into tbl_book
(
	id
	, title
	, author
	, publisher
	, category
)
values 
(
	seq_id.nextval
	, #{title}
	, #{author}
	, #{publisher}
	, #{category}
)

mapper.update:
update tbl_book
set title = #{title},
author = #{author},
publisher = #{publisher},
category = #{category}
where id = #{id}
