mapper.select:
SELECT N_NO , N_TITLE , N_CONTENT , TO_CHAR(N_ENROLL_DATE,'YYYY-MM-DD') N_ENROLL_DATE , M_NO FROM ( SELECT ROWNUM RNUM, NLIST.* FROM( SELECT * FROM NOTICE WHERE N_STATUS='Y'
AND N_TITLE LIKE '%' || #{ searchValue, jdbcType=VARCHAR } || '%'
ORDER BY N_NO DESC) NLIST ) WHERE RNUM BETWEEN #{ startRow } AND #{ endRow }
